 VALUES ('00000000-0000-0000-006e-000000000514' ,N'Riesenkrake' ,NULL ,N'' ,8 ,N'1W6' ,2 ,2 ,300 ,100 ,0 ,0 ,25 ,10 ,18 ,9 ,2 ,NULL ,2 ,2 ,2 ,2 ,2 ,2 ,2 ,2 ,19 ,NULL ,NULL ,NULL ,NULL ,N'Tier, Kopffüßer, Krake' ,N'Größe: 6 Schritt Rumpf + 10 Schritt Fangarme Gewicht: bis 20 Quader
