 VALUES ('00000000-0000-0000-006e-000000000521' ,N'Risso (erfahren)' ,NULL ,N'/DSA MeisterGeister;component/Images/Wesen/risso.png' ,11 ,N'1W6' ,2 ,12 ,27 ,34 ,0 ,0 ,14 ,6 ,6 ,9 ,7 ,NULL ,1 ,1 ,1 ,1 ,1 ,1 ,1 ,1 ,NULL ,NULL ,NULL ,NULL ,NULL ,N'Kultur, Fisch, Fischmensch' ,N'Größe: um 1,70 Schritt Gewicht: um 60 Stein
