-- Zauber-Tabelle
CREATE TABLE Zauber (ZauberID int IDENTITY (1, 1) NOT NULL PRIMARY KEY, Name nvarchar(200), Eigenschaft1 nvarchar(2), Eigenschaft2 nvarchar(2), Eigenschaft3 nvarchar(2), Komplex nvarchar(2), Repräsentationen nvarchar(300), Merkmale nvarchar(300), Quelle nvarchar(100));
CREATE TABLE Held_Zauber (HeldID int NOT NULL, ZauberID int NOT NULL, ZfW int DEFAULT 0, Repräsentation nvarchar(50) NOT NULL DEFAULT 'Mag', Bemerkung nvarchar(300), PRIMARY KEY(HeldID, ZauberID, Repräsentation));
ALTER TABLE Held_Zauber ADD CONSTRAINT Zauber_FK FOREIGN KEY (ZauberID) REFERENCES Zauber (ZauberID) ON DELETE cascade ON UPDATE cascade;
ALTER TABLE Held_Zauber ADD CONSTRAINT Held_Zauber_HeldFK FOREIGN KEY (HeldID) REFERENCES Held (HeldID) ON DELETE cascade ON UPDATE cascade;

