 VALUES ('00000000-0000-0000-0009-000000000155' ,N'Fußfeger' ,1 ,N'AT+X; X=Zuschlag auf PA und/oder GE-Probe des Gegners; ohne Beinarbeit ist die PA nochmal um +4 erschwert, Balance erleichtert um +2, Herausragende Balance um +4 und Standfest um +1 (beide Proben); Bei Misslingen der GE ist der Gegner am Boden' ,N'WdS 91');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000156' ,N'Gerade' ,1 ,N'AT+X; bei Gelingen: TP(A) +X, ohne Auspendeln ist die PA um +4 erschwert' ,N'WdS 91');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000157' ,N'Griff' ,1 ,N'AT+X; Gegner erleidet Zuschläge von (X/2) auf Manöver, Angriffs- & Abwehraktionen solange der Griff hält, kann durch PA abgewehrt werden erschwert um (X/2), kann durch AT+(X/2) abgeschüttelt werden (erzeugt keine TP(A))' ,N'WdS 91');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000158' ,N'Halten' ,1 ,N'AT+X oder PA+X, nächste gegnerische Aktion ist um +X erschwert' ,N'WdS 91');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000159' ,N'Handkante' ,1 ,N'AT+X, TP(A)+X, wenn die TP (nicht TP(A)) höher sind als KO/2, dann entsteht eine Wunde; Erhöhung der Wundschwelle im Nahkampf zählt hier nicht !' ,N'WdS 91');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000160' ,N'Hoher Tritt' ,1 ,N'AT+X, TP(A)+X, ohne Auspendeln oder Kreuzblock (oder Ausweichen oder Waffenparade) ist die PA um +4 erschwert' ,N'WdS 91');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000161' ,N'Klammer' ,1 ,N'AT+X, Gegner Ringen-PA+X oder KK-Probe+doppelte KK-Differenz zum Angreifer (mind. +X), beide können nur Biss, Kopfstoß, Knie, Schwitzkasten, Würgegriff einsetzten, der Geklammerte hat immer +X' ,N'WdS 91');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000162' ,N'Knaufschlag' ,1 ,N'AT+X mit einem Waffenknauf, Schaden: 1W6+2 TP(A), X=1W6+2+X TP(A) oder PA+X' ,N'WdS 91');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000163' ,N'Knie' ,1 ,N'AT+X, ohne Beinarbeit, Kreuzblock oder Ausweichen ist die PA+4, Frauen und Achaz: 1W6+X TP(A), Männer: 1W6+2+X TP(A), wenn SP(A) > Wundschwelle 1W3 KR kampfunfähig, INI-2W6, Verlust aller Aktionen dieser KR' ,N'WdS 91');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000164' ,N'Kopfstoß' ,1 ,N'AT+X, TP+X, Parade eines Waffenträgers ergibt die vollen TP der Waffe (ohne TP/KK), mit einem Metalhelm: 1W6+2+X TP(A)' ,N'WdS 91');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000165' ,N'Kreuzblock' ,1 ,N'PA+X, die Ansage X kann sowohl auf die eigene AT als auch auf die Gegner-PA verteilt werden, reguläre Parade gegen Knie, Tritt und Sprungtritt' ,N'WdS 91');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000166' ,N'Niederringen' ,1 ,N'AT+X, gegnerische PA erschweren und/oder nächste AT erleichtern, beide Kämpfer am Boden' ,N'WdS 91');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000167' ,N'Schmetterschlag' ,1 ,N'AT+X, mehr SP(A) als Wundschwelle  =KO-Probe, sonst 1W6 SR bewusstlos, wenn TP(A) > KO dann steht keine KO-Probe zu, keine Erhöhung der Wundschwelle um +2 !' ,N'WdS 92');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000168' ,N'Schmutzige Tricks' ,1 ,N'kann Umgebung im Kampf einsetzen (Meisterentscheid)' ,N'WdS 92');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000169' ,N'Schwanzfeger' ,1 ,N'AT+X, X=PA-Erschwernis oder GE-Erschwernis, ohne Beinarbeit ist PA+4' ,N'WdS 92');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000170' ,N'Schwanzschlag  ' ,1 ,N'Differenz AT-Wert und AT-Wurf / 2 gilt als PA-Erschwernis' ,N'WdS 92');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000171' ,N'Schwinger' ,1 ,N'AT+X, bei Gelingen ist die PA+X erschwert, ohne Auspendeln ist die PA um weitere +4 erschwert' ,N'WdS 92');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000172' ,N'Schwitzkasten' ,1 ,N'Beginnt mit einer Ringen-AT und misslungene Ringen-PA, jede weitere Runde erleichtert sich die AT um 1, die Parade erschwert sich um 1, die TP(A) steigen um +1 (nicht die TP), endet wenn AT misslingt oder PA gelingt, Verteidiger kann nur Biss, Knie oder Schwanzschlag einsetzten jedoch um +4 erschwert' ,N'WdS 92');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000173' ,N'Sprung' ,1 ,N'PA+X (jedoch nur für Sprungtritt oder Schwanzschlag nutzbar), Handgemenge +4, Nahkampf +2 (+2 gegen Bewaffnete), Misslingen: GE-Probe+echte TP-2 (Standfest)-(2 Balance oder -4 Herausragende Balance), am Boden, Gelingen: -4 INI' ,N'WdS 92');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000174' ,N'Sprungtritt' ,1 ,N'AT+4+X, Punkte aus Sprung nutzbar, Ausweichen, Kreuzblock oder Schildparade, Waffenparade +4, waffenlose PA sind nicht erschwert, Schaden: TP(A) +2+X, bewaffnete PA = voller Schaden, misslingt der Sprungtritt GE-Probe, Gelingen: INI-4, misslingen am Boden' ,N'WdS 92');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000175' ,N'Tritt' ,1 ,N'AT+X, TP+X, ohne Beinarbeit ist die PA+4 erschwert' ,N'WdS 92');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000176' ,N'Versteckte Klinge' ,1 ,N'kann DK H-Waffe mit Raufen-AT/-PA einsetzten, kann mit Gerade und Schwinger kombiniert werden' ,N'WdS 92');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000177' ,N'Würgegriff' ,1 ,N'einmalige AT zu Beginn, jede KR automatisch 1W6+2 TP(A), keine Parade für den Verteidiger, aber Raufen- oder Ringen-AT+2 (nicht parierbar vom "Angreifer")' ,N'WdS 93');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000178' ,N'Wurf' ,1 ,N'AT+4+X, Halten oder Griff muss vorausgehen,' ,N'WdS 93');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000179' ,N'Eisenhagel' ,1 ,N'kann bis zu 5 Wurfscheiben mit Erschwernis Anzahlx2 auf ein Ziel werfen (+5 pro weiteres Ziel)' ,N'WdS 95');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000180' ,N'Scharfschütze ( )' ,1 ,N'Ansage/2=Aktionen, Aufschläge/3 gezielter Schuss, max. Ansage in Höhe FK-TaW (alles zu TP), Schnellschüsse +1' ,N'WdS 95');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000181' ,N'Schnellladen (Armbrust)' ,1 ,N'Ladezeit -¼ der Aktionen (aufrunden) (Minimum 1 Aktion)' ,N'WdS 95');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000182' ,N'Schnellladen (Bogen)' ,1 ,N'Ladezeit -1 Aktion (Minimum 1 Aktion)' ,N'WdS 95');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000183' ,N'Nekrotischer Griff' ,1 ,N'Der Griff eines Untoten lässt berührte Gliedmaßen verkümmern und absterben. Zusätzlich zu einem Folgeschaden von 1 SP für 3 KR liegt die Wundschwelle des Opfers für diese Angriffe um 2 niedriger. Bereits zwei Wunden an einem Körperteil lassen es absterben.' ,N'VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000184' ,N'Berittener Schütze' ,1 ,N'Aufschläge/2 bei schießen vom Pferd, spannen auf Pferd' ,N'WdH 117 ');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000185' ,N'Geschützmeister' ,1 ,N'Nachladezeit sinkt auf ¾, +2 auf die Fernkampf-Probe' ,N'WdH 279 ');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000186' ,N'Improvisierte Waffen' ,1 ,N'alle Manöver mit improvisierter Waffen, Patzer auf 20' ,N'WdH 74');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000187' ,N'Kriegsreiterei' ,1 ,N'SF Reiterkampf keine Aufschläge bei Reiterkampf, Lanzenreiten-2 (nicht kumulativ), Orientieren-1, max. Ansage in Höhe AT' ,N'WdH 101 ');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000188' ,N'Reiterkampf' ,1 ,N'Aufschläge/2 bei Reiterkampf, Sturmangriff gegen Fußkämpfer' ,N'WdH 101');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000191' ,N'Belebte Gliedmaßen' ,0 ,N'Abgetrennte Gliedmaßen des Untoten kämpfen eigenständig weiter. Der Anblick ist so erschreckend, dass auch weitere Angriffe gegen den Untoten wie unter Schreckgestalt I behandelt werden.' ,N'VTuU 108');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000192' ,N'Dunkelheit' ,0 ,N'Um das Wesen herum befindet sich eine Zone der Dunkelheit, die alles nicht übernatürliche Licht verschluckt, wie DUNKELHEIT.' ,N'VTuU 108');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000193' ,N'[Elementarer] Griff (Stufe)' ,0 ,N'Die Füße oder Hände des Untoten sind mit elementarer Kraft umgeben. Jeder Angriff richtet 1 SP pro Stufe extra bei Gegnern an.' ,N'VTuU 108');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000197' ,N'Einlullen' ,0 ,N'Durch einen hypnotischen Blick, Gesang oder Sekret ermüden die Opfer langsam. Jede SR ist eine Selbstbeherrschungs-Probe notwendig um nicht einzuschlafen. Der Schlaf ist voller Alpträume und ohne Regeneration.' ,N'VTuU 108');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000198' ,N'Erhöhtes Infektionsrisiko (Stufe)' ,0 ,N'Zu allen W20-Würfen, die durchgeführt werden, um die Infektion mit einer Krankheit festzustellen, wird die Stufe dieser Eigenschaft addiert.' ,N'VTuU 108');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000199' ,N'Ersäufen (Untot)' ,0 ,N'Der Untote kann seinen Gegner unter Wasser ziehen oder pressen. Der Untote kann maximal einen Gegner gleichzeitig unter Wasser halten.' ,N'VTuU 108');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000202' ,N'Fleischtasche' ,0 ,N'Der Untote kann kleine Objekte in seinem Leib oder Gerippe verbergen. Für eine auf diese Art versteckte Waffe (max. DK H) verfügt er über die SF Schnellziehen.' ,N'VTuU 108');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000203' ,N'Flimmernde Körperlosigkeit' ,0 ,N'Der Untote verliert zeitweilig seine körperliche Form und ist in dieser Zeit nur noch als geisterhafte Gestalt wahrnehmbar.' ,N'VTuU 108');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000205' ,N'Ghulgift' ,0 ,N'Der Biss eines Ghuls ist giftig (Stufe 10, pro KR fallen KK und GE je -1 für 2W20 KR). Fällt KK oder GE dabei auf 0, ist das Opfer vollständig gelähmt wie unter der Eigenschaft Lähmung. Erst nach drei Stunden regenerieren sich die Werte um 1 pro SR. Pro 5 SP durch einen Biss besteht eine 5% Chance, dass die Verwandlung in einen Ghul eintritt.' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000206' ,N'Grabeklauen' ,0 ,N'Durch seine Pranken oder andere natürliche Grabinstrumente kann das Wesen Tunnel oder Erdlöcher graben. Die Pranken richten +1 TP im waffenlosen Kampf an.' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000207' ,N'Halluzinationen' ,0 ,N'Der Blick, geistige Kontakt, ein Sekret o.ä. lösen beim Opfer Halluzinationen aus, die auf erzdämonische Weise pervertiert und schrecklich sind. Das Opfer ist durch die Macht der Herrin der Alpträume in Tagträumen gefangen, von denen alle fünf Sinne betroffen sind. Die Realitätsdichte entspricht der doppelten Kontrollschwierigkeit des Untoten.' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000208' ,N'Immobil' ,0 ,N'Der Untote ist unbeweglich, hat keine PA und keine Fähigkeiten zum Ausweichen oder anderen defensiven Aktionen.' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000209' ,N'Infektion mit [Krankheit]' ,0 ,N'durch Berührung kann der Untote ein Opfer mit einer Krankheit infizieren' ,N'WdZ 234');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000210' ,N'Immunität gegen kritische Treffer (Untot)' ,0 ,N'Gegen den Untoten sind kritische Treffer nicht möglich. Sie zählen als glückliche Attacke.' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000212' ,N'Klebriges Sekret' ,0 ,N'Ein aus der Haut oder dem Speichel abgesondertes Sekret des Untoten kann als klebender Stoff verwendet werden, um z.B. die Verriegelung von Türen zu verstärken o.ä. Auf Flächen, über die der Untote sich bewegt, kann man sich verfangen.' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000213' ,N'Krallen (Stufe)' ,0 ,N'Der Untote erhält besonders scharfe und mächtige Krallenhände. Diese verursachen je Stufe einen zusätzliche TP bei waffenlosen Angriffen (max. 3). Weiterhin schlagen die Krallen fürchterliche Wunden (WS-1 pro Stufe).' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000215' ,N'Lähmender Biss' ,1 ,N'Der Untote kann mit seinem Biss Opfer lähmen. Je KR sinkt dabei KK und GE um je 1 für W6 Runden. Sinkt einer der Werte auf 0 ist das Opfer für mehrere Stunden gelähmt.' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000216' ,N'Lähmende Berührung' ,0 ,N'Der Untote kann mit seiner Berührung Opfer lähmen. Je KR sinkt dabei KK und GE um je 1 für W6 Runden. Sinkt einer der Werte auf 0 ist das Opfer für mehrere Stunden gelähmt.' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000217' ,N'Lähmender Blick' ,0 ,N'Der Untote kann mit seinem Blick Opfer lähmen. Je KR sinkt dabei KK und GE um je 1 für W6 Runden. Sinkt einer der Werte auf 0 ist das Opfer für mehrere Stunden gelähmt.' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000218' ,N'Lähmender Schrei' ,1 ,N'Der Untote kann mit seinem Schrei Opfer lähmen. Je KR sinkt dabei KK und GE um je 1 für W6 Runden. Sinkt einer der Werte auf 0 ist das Opfer für mehrere Stunden gelähmt.' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000224' ,N'Lebenshauch (Untot)' ,0 ,N'Die Anatomie und der Geist des Untoten sind noch nahe genug am Leben, so dass einige motorische und erlernte Fähigkeiten noch fast so gut arbeiten wie zu Lebzeiten. Der halbe TaW aller Kampf- und körperlichen Talente bleiben erhalten, ebenso ein Viertel des TaW der handwerklichen Talente.' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000225' ,N'Limbuswanderer' ,0 ,N'Der Untote hat die Macht durch den Limbus zu reisen, in dem er ein AUGE DES LIMBUS öffnet und die Sphären damit zerreißt.' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000226' ,N'Lockruf' ,0 ,N'Durch hypnotische Geräusche, einen süßen, aber tödlichen Gesang oder auf andere Art, kann das Untote Geschöpf Opfer zu sich locken.' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000227' ,N'Resistenz gegen Wunden' ,0 ,N'Der Untote wird von der Auswirkung von Wunden kaum betroffen. Die Wundschwelle ist verdoppelt.' ,N'VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000228' ,N'Geisternebel' ,0 ,N'Der Auftritt des Untoten ist mit gespenstischem Nebel verbunden, der ihn etwa 5 Schritt Radius umgibt. Die Sichtverhältnisse sind entsprechend eingeschränkt. Es sind Braggueske Fratzen und die scheinbaren Geister verstorbener Seelen zu sehen, deren Anblick wie eine Schreckgestalt I wirkt.' ,N'VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000230' ,N'Nekrotischer Biss' ,0 ,N'Der Biss eines Untoten lässt berührte Gliedmaßen verkümmern und absterben. Zusätzlich zu einem Folgeschaden von 1 SP für 3 KR liegt die Wundschwelle des Opfers für diese Angriffe um 2 niedriger. Bereits zwei Wunden an einem Körperteil lassen es absterben.' ,N'VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000231' ,N'Parasit' ,0 ,N'Im Leib des Untoten hat sich ein Parasit niedergelassen, der den Körper mit hm teilt. Es handelt sich um ein eigenständig agierendes Wesen, das aber nicht ohne den Wirt existieren kann. Auf bestimmten Wegen kann sich der Parasit auf ein lebendes Opfer übertragen und es angreifen.' ,N'VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000232' ,N'Pestodem' ,0 ,N'Der Untote kann mit einer Aktion eine Wolke üblen Pestgeruchs ausstoßen, der wie ein TLALUCS ODEM mit ZfP* in Höhe der Kontrollschwierigkeit des Untoten wirkt.' ,N'VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000233' ,N'Pestspeichel' ,0 ,N'Der Untote kann mit einer Aktion eine Wolke üblen Pestgeruchs ausstoßen, der wie eine Miasmafaxius-Variante in Höhe von (1/3 Kontrollschwierigkeit Würfeln)  des Untoten wirkt.' ,N'VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000234' ,N'Pestsporen' ,0 ,N'Der Untote kann mit einer Aktion eine Wolke üblen Pestgeruchs ausstoßen, der wie eine Miasmasphaero-Variante mit ZfP* in Höhe der Kontrollschwierigkeit des Untoten wirkt.' ,N'VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000235' ,N'Rauch' ,0 ,N'Etwa 5 Schritt um den Untoten herum befindet sich dicker, schwarzer Rauch. Die Sichtverhältnisse sind eingeschränkt (+4 auf FK-Proben) und ein Aufenthalt im Rauch verursacht 1W6-1 SP(A) pro KR. Im Nahkampf mit dem Untoten befindet man sich in allen Distanzklassen innerhalb des Wirkungsbereichs' ,N'VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000239' ,N'Säure-Geifer' ,0 ,N'Dem Untoten ist es möglich, ein ätzendes Sekret abzusondern, das sich durch Materialien aller Art fressen kann. Bisse eines solchen Wesens erhöhen die TP um 2 und verursachen einen Folgeschaden von 1 SP pro KR für 3 KR. Für insgesamt 10 TP durch diesen Schaden sinkt der RS des Opfers um 1.' ,N'VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000240' ,N'Schreckgeheul I' ,0 ,N'Der Untote heult, kreischt oder brüllt auf dermaßen erschreckende Weise, dass jeder, der ihn hört, wie von einer Schreckgestalt I betroffen ist.' ,N'VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000241' ,N'Schreckgeheul II' ,0 ,N'Der Untote heult, kreischt oder brüllt auf dermaßen erschreckende Weise, dass jeder, der ihn hört, wie von einer Schreckgestalt II betroffen ist.' ,N'VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000242' ,N'Schwächende Aura' ,0 ,N'Das Opfer des Untoten wird geschwächt: Die Auro erfordert keine Berührung und erfordert bei allen Gegner in DK S oder niedriger eine KO-Probe +3 (DK H), +2 (DK N) oder +1 (DK S). Bei Misslingen erleidet das Opfer für 1 Punkt Abzug auf KK.' ,N'VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000243' ,N'Schwächende Berührung' ,0 ,N'Das Opfer des Untoten wird geschwächt: Bei Berührung oder erfolgreichem waffenlosen Angriff, der auch Schaden verursacht, erfolgt eine KO-Probe. Mei Misslingen erleidet das Opfer für 1W6 KR je 1 Punkt Abzug auf KK pro KR.' ,N'VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000244' ,N'Schwarmkörper' ,0 ,N'Der Körper des Untoten kann sich innerhalb einer Aktion für 2W6 KR in einen Scharm aus Untoten Tieren verwandeln, deren Werte wie bei einer Rotte modifiziert sind.' ,N'VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000249' ,N'Ängste auslösen (bestimmte Angst oder Schlechte Eigenschaft Aberglaube)' ,0 ,N'MU-Probe oder schlechte Eigenschaft erschwert um diesen Wert' ,N'EG 143');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000250' ,N'Artefaktbeseelung' ,0 ,N'Elemtn ist besonders für den Dinest Bindung in Artfekate geeignet, d. h. alle Proben sind um die Stufe (max. 3) erleichtert. Schlüsselwerte werden um die Stufe erhöht (Boni auf TP, RS, Talente oder Zauber) wenn sie affin zum Element sind. Patzerwahrscheinlichkeit liegt allerdings bei (20-Stufe) bei 20.' ,N'EG 143');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000251' ,N'Aura (Element)' ,0 ,N'Jede KR richtet die Aura Schaden an. In DK S: 1 SP, in DK N: 2 SP und in DK H: 3 SP. ' ,N'EG 143 / VTuU 108');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000252' ,N'Blinkende Unsichtbarkeit' ,0 ,N'Wesen macht sich schlagartig unsichtbar (PA Wert 18). Die Unsichtbarkeit dauert W6 KR.' ,N'EG 143');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000253' ,N'Ersäufen' ,0 ,N'Wesen zieht Gegner unter Wasser. Gelingt Angriff erleidet Opfer pro KR 1W6 TP (A). Jede KR kann das Opfer eine KK-Probe ablegen, die um die Anzahl KR unter Wasser erschwert ist. Bei einem Treffer von 10+ TP löst das Elementar seinen Griff. Das Elementar kann max. 1 Gegner unter Wasser halten.' ,N'EG 143');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000254' ,N'Elementare Gewalt' ,0 ,N'Die Angriffe von Elementaren richten magischen und elementaren (je nach Element) Schaden an, hinzukommen noch die elementaren Sekundäreffekte' ,N'EG 143');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000255' ,N'Elementare Fessel' ,0 ,N'Am Hals schnürt es Luft ab (Pro KR 1W3 TP(A) und 1W3 SP pro KR. Um Handgelenk sind alle Aktionen im Kampf (AT und PA) sowie Zauber mit Gesten um Beherrschungsschwierigkeit erschert. Um Fuß sinken GE und GS um Beherrschungsschwierigkeit. Proben gegen Niederwerfen sind um Beherrschungsschwierigkeit erschwert. An Händen/Beinen erleidet das Opfer nur 1W3-1 SP pro KR. Selbstbeherrschungs-Proben sind während der Wirkung um die Beherrschungsschwierigkeit des Elementarwesens erschwert, MU-Proben um Beherrschungsschwierigkeit /2.' ,N'EG 143');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000256' ,N'Elementares Inferno' ,0 ,N'In einer Zone von 4+Beherrschungsschwierigkeit Schritt Radius schießt ein elementares Inferno hervor, das alle Wesen im Wirkungsbereich erfasst und ihnen 3W6+ZfP* TP zufügen. Zusätzlich kommt es zu elementaren Sekundärschäden. Der Einsatz der Eigenschaft dauert 8 Aktionen und kostet 25 AsP.' ,N'EG 143');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000257' ,N'Elementarer Griff (Stufe)' ,0 ,N'Jeder Angriff, den das Elementarwesen mit seinem Körper ausführt, richtet 1 SP pro Stufe extra bei Gegnern an, zusätzlich elementare Sekundäreffekte und Folgeschaden von 1 SP pro KR für 3 KR. Das Berühren beziehungsweise Überlaufen von zum Beispiel brennbaren Gegenständen kann diese entzünden (Feuer), Wasseroberflächen erstarren lassen (Eis) und Ähnliches je nach Element.' ,N'EG 143');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000258' ,N'Elementare Regeneration' ,0 ,N'Elementarwesen regenerieren pro Stunde Aufenthalt in ihrem Element einen LeP und 1W6 AsP.' ,N'EG 143');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000259' ,N'Elementares Wachstum' ,0 ,N'Das Elementarwesen wird durch die Nähe zu einem bestimmten Element langsam größer und mächtiger. Es erhält permanente LeP in Höhe von 1 pro 3 Monate Aufenthalt in einem Gebiet von elementarer Urkraft. Pro 15 so gewonnene LeP über den Grundwert hinaus erhält das Wesen +1 auf AT, PA, INI und TP. Je 50 LeP über den Grundwert hinaus steigern die DK sowie die Größenkategorie des Wesens um eine Stufe.' ,N'EG 143');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000260' ,N'Elementarkritische Konsistenz' ,0 ,N'Elementare Wesen mit dieser Eigenschaft gehen sofort in ihrem Element auf, wenn sie durch einen einzelnen Angriff durch ihr eigenes Element die Hälfte ihrer LeP verlieren würden' ,N'EG 144');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000261' ,N'Flächenangriff mit [Element]' ,1 ,N'Mit dieser Eigenschaft kann ein Elementarwesen einen flächendeckenden Angriff mit seinem Element durchführen. Die Wirkung entspricht der eines IGNISPHAERO mit ZfP* in Höhe seiner Kontrollschwierigkeit inklusive eventueller Sekundäreffekte je nach Element. Der Einsatz dieser Eigenschaft dauert 7 Aktionen und kostet das Elementarwesen 15 AsP.' ,N'EG 144 / VTuU 108');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000262' ,N'Flugfähigkeit' ,0 ,N'Das Elementarwesen kann fliegen und sich mit der angegebenen Geschwindigkeit durch die Luft bewegen. Diese Sonderfertigkeit kommt bei Erzelementaren nur automatisch vor und kann nicht nachträglich verliehen werden.' ,N'EG 144');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000263' ,N'Frost' ,0 ,N'Ein oder mehrere Gegenstände (max. 50 Stein) werden eingefroren dass jede Berührung schmerzt. Um sie in der Hand zu halten ist eine Selbstbeherrschungs-Probe nötig, die um die Beherrschungsschwierigkeit des Elementarwesens erschwert ist. Pro KR verursacht dies 1 SP. Ein am Körper getragener Gegenstand richtet pro  KR für jeden RS 1 SP. Die Wirkung tritt augenblicklich ein und kostet das Elementarwesen 1 AsP pro KR pro 10 Stein Gewicht der Gegenstände.' ,N'EG 144');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000264' ,N'Gesang' ,0 ,N'Elementar zieht damit den Zuhörer in seinen Bann (als wirke Große Gier mit 12 ZfP*). Der Zuhör verspürt einen starken Drang sich dem Elementar zu nähern. Es vergisst es seine Umgebung, Menschenverstand und manchmal sogar den Überlebenswillen. Damit die Wirkung sich entfaltet, muss der Gesang min. 10 KR aufrechtgehalten und vom Opfer gehört werden. Wirkung fällt erst dann ab, wenn Gesang unterbrochen oder die Wirkung mittels Liturgien oder Antimagie (BEHERRSCHUNG BRECHEN, EINFLUSS BANNEN, aber auch der SILENTIUM kann hier wahre Wunder wirken) aufgehoben wird. Pro Opfer, den der Gesang fesseln soll, muss das Elementarwesen 5 AsP aufwenden.' ,N'EG 144');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000265' ,N'Gestank' ,0 ,N'Einige pervertierte Elementare haben einen Gestank, der große Übelkeit verursacht. Es muss eine KO-Probe, erschwert um einen eventuellen Sensiblen Geruchssinn, abgelegt werden. Bei Misslingen muss man sich übergeben und verliert für 2 KR alle Angriffsaktionen. Zudem erleidet man –1W6 Punkte auf MU, AT, PA, FK und INI-Basis (keine Neuberechnung der Werte). Außerhalb der Geruchsweite (ca. TaW Sinnenschärf ein Schritt Entfernung) bauen sich die Mali um 1 Punkt pro SR wieder ab.' ,N'EG 144 / VTuU 108');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000266' ,N'Glut' ,0 ,N'Ein oder mehrere Gegenstände (max. 50 Stein) werden erhitzt dass jede Berührung schmerzt. Um sie in der Hand zu halten ist eine Selbstbeherrschungs-Probe nötig, die um die Beherrschungsschwierigkeit des Elementarwesens erschwert ist. Pro KR verursacht dies 1 SP. Ein am Körper getragener Gegenstand richtet pro  KR für jeden RS 1 SP. Die Wirkung tritt augenblicklich ein und kostet das Elementarwesen 1 AsP pro KR pro 10 Stein Gewicht der Gegenstände.' ,N'EG 144');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000267' ,N'Immunität gegen Kritische Treffer' ,0 ,N'Das Elementare Wesen wird von der Auswirkung von kritischen Attacken nicht anders als von normalen Angriffen betroffen, da seine Anatomie keine besonders verwundbaren Stellen aufweist.' ,N'EG 144');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000268' ,N'Immunität gegen Wunden' ,0 ,N'Das Elementarwesen / der Untote ist von der Auswirkung von Wunden nicht betroffen, ebenso wenig erleidet es Einbußen durch niedrige LeP.' ,N'EG 144 / VTuU 109');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000269' ,N'Lebenshauch' ,0 ,N'Das Elementare Wesen ist in der Lage, selbst schwerste Verletzungen zu heilen. Es kann pro Tag bis zu 10W6 SP heilen, die beliebigem aufgeteilt werden können. Für je 7 Punkte wird außerdem eine Wunde geheilt. Sogar verlorenes Sikaryan kann auf diese Weise mit bis zu 10 Punkten pro Tag wieder hergestellt werden.' ,N'EG 144');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000270' ,N'Kochendes Blut' ,0 ,N'Wenn ein Wesen mit dieser Eigenschaft durch eine Verletzung 10 oder mehr LeP auf einmal verliert oder es getötet wird, spritzt sein heißes Blut umher und verursacht 3W6 TP bei allen mit ihr Kämpfenden in DK H oder N. Ausweichen ist möglich.' ,N'EG 144');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000271' ,N'Nebel' ,0 ,N'Der Auftritt des Elementarwesen ist mit dichtem Nebel verbunden, der es in etwa 5 Schritt Radius umgibt. Die Sichtverhältnisse sind entsprechend eingeschränkt (+4 auf FK-Proben gegen das Elementarwesen). Bei pervertierten Elementaren verzerrt sich der Nebel oft zu unheimlichen Fratzen oder bildet gefährlich aussehende (aber harmlose) Tentakeln aus.' ,N'EG 144 / VTuU 110');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000272' ,N'Rauch' ,0 ,N'Etwa 5 Schritt um das elementare Wesen herum befindet sich dicker, schwarzer Rauch. Die Sichtverhältnisse sind eingeschränkt (+4 auf FK-Proben gegen das Wesen) und ein Aufenthalt im Qualm verursacht 1W6–1 SP(A) pro KR. Im Nahkampf mit dem Wesen befindet man sich in allen Distanzklassen automatisch innerhalb des Wirkungsbereichs.' ,N'EG 144');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000273' ,N'Steinhaut' ,0 ,N'Die Haut des Wesens ist so hart, dass der RS um die Stufe der Eigenschaft (maximal 6) erhöht wird. Nur Waffen mit TP* (Geschütz, s. Arsenal S. 49) richten noch normal Schaden an, gegen alle anderen Waffen besteht eine Resistenz (halber Schaden). Je 3 Punkte auf diese Art gewonnene RS steigern die TP waffenloser Angriffe (und solcher mit natürlichen Waffen) um 1. INI und GS sinken dagegen um Stufe/2 (Minimal auf 1).' ,N'EG 145');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000274' ,N'Starre' ,0 ,N'Das Wesen kann binnen 1 Aktion in Tagesstarre verfallen, wodurch es unbeweglich wird und in jeder Hinsicht als Statue gilt, aber seine Umgebung weiter wahrnimmt. Binnen 5 Aktionen kann es in eine bis zu Jahrzehnte anhaltende Lange Starre verfallen, in der er lediglich größere Beschädigungen an seinem Körper wahrnimmt, nicht weiter altert, aber automatisch einmalig um eine Alterungsstufe aufsteigt. Es hat statt LeP in dieser Form LeP*2 StP, RS*10 Härte und Struktur je nach Haltung 6-12 (W6+6). Es kann in dieser Form keinen Schaden aus Hunger, Ersticken, Krankheit oder Gift erhalten. Binnen 1 Aktion (Tagesstarre) bzw. 5 Aktionen (Lange Starre) kann es wieder erwachen, wodurch Beschädigungen zu Narben werden. Eine Zerstörung der Statue tötet auch das Wesen. Wesen, die diese Eigenschaft automatisch besitzen, fallen zudem in hohem Alter in eine Letzte Starre, aus der sie lediglich durch z.B. Sikaryan-Zuführung oder sphärische Entladungen wieder erwachen können. Sie erstarren außerdem mit hohem Alter zunehmend. Je Alterungsstufe sinken IN, GE, AT, INI und GS um 1 (GS bleibt minimal 1). KK, RS, TP und Niederwerfen steigen hingegen je um 1, Hinterhalt um 3, KL um 1 je 2 Alterungsstufen. Eventuelle Flugfähigkeit ist ab GE 3 nicht mehr möglich. Sobald die AT auf 0 gesunken ist, kommt es zur letzten Starre.' ,N'EG 145');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000275' ,N'Steinfraß' ,0 ,N'Durch das Verspeisen bestimmter Gesteinsarten kann sich das Wesen stärken. Es erhält für 2W6+Stufe SR einen um die Stufe der Fähigkeit erhöhten Wert in Eigenschaften abhängig von der Art des Steins. Es kann jeweils nur unter der Wirkung einer einzelnen Gesteinsart gleichzeitig stehen. Gesteinsart Betroffener Wert: Tuffstein GE, Granit KK, Marmor CH, Schiefer KO, Sandstein FF, Kalkgestein KL, Quarz IN, Basalt MR, Kreidefels KK (um Stufe gesenkt;wird Opfern rituell zum Fraß gegeben). Halbedelsteine Alle 4 körperlichen Eigenschaften um Stufe erhöht Edelsteine Alle 4 geistigen Eigenschaften um doppelte Stufe erhöht. Die Fähigkeit kann in einem Ritual auch auf andere Mitesser übertragen werden. Diese müssen jedoch über die für das Zerbeißen von Steinen nötigen Beißwerkzeuge verfügen. Es soll eine Variante für das Element Eis geben, bei dem bestimmte Arten von Eis ähnliche Effekte bewirken.' ,N'EG 145');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000276' ,N'Unbelebt' ,0 ,N'Das Wesen gilt als unbelebt, ist also immun gegen Zauber wie den EXPOSAMI.' ,N'EG 145');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000277' ,N'Verschmelzen mit Element' ,0 ,N'Das Wesen kann jederzeit innerhalb 1 KR mit seinem Element verschmelzen, wodurch es faktisch nicht mehr sichtbar und sicher vor Schaden ist. Antimagie oder die Vernichtung des Gegenstands, in dem es sich verbirgt, bringen es wieder zum Vorschein und können ihm Schaden verursachen. Es kann sich mit dieser Fähigkeit jedoch nicht innerhalb des Elementes weiter bewegen.' ,N'EG 145');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000278' ,N'Versinken' ,0 ,N'Der Boden unter dem Opfer (Beherrschungsschwierigkeit als Radius) wird zu einem Strudel oder matschigen Pfuhl, der es in die Tiefe zieht. Im Wirkungsbereich sinken GS und GE um 1 je KR. Sinkt die GE auf 0, ist das Opfer untergegangen und erleidet Schaden wie beim Ertrinken (WdS 140). Jede KR ist eine GEProbe möglich, um sich zu befreien (gesenkte GE beachten). Ist es untergegangen, können nur noch andere Personen helfen.' ,N'EG 145');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000279' ,N'Wildwuchs' ,0 ,N'Ein lebendes Wesen wächst um eine Größenkategorie mit allen daraus entstehenden Auswirkungen. KK und KO steigen um je 3, LeP um 5, RS und TP um 1. Im Falle von Pflanzen steigt die Größe um zwei Kategorien, die Pflanze wirkt evtl. durch dichteres Blattwerk wie eine WAND AUS DORNEN (mit Beherrschungsschwierigkeit ZfP*), die Wirkung von Kräutern wird verdoppelt (Heilpflanzen, Giftpflanzen, aber auch TP und LeP von gefährlichen Pflanzen). Die Verwandlung dauert 6 Aktionen und hält für 1 SR pro 5 eingesetzte AsP des Wesens an.' ,N'EG 146');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000280' ,N'Zerbersten (Element)' ,0 ,N'Wenn die LeP des Elementarwesens auf unter 0 sinken, explodiert sein Leib in einem hässlichen, elementaren Inferno und richtet in seiner Umgebung einen Schaden von 4W6+4 (in DK H) an. Der Schaden sinkt um 1W6+1 je DK weiter entfernt vom Ziel. Darüber hinaus treten eventuelle elementare Sekundäreffekte auf.' ,N'EG 146');
INSERT INTO [Kampfregel] (  [KampfregelGUID],  [Name],  [Manöver],  [Bemerkung],  [Literatur]) 
 VALUES ('00000000-0000-0000-0009-000000000281' ,N'Zerschellender Panzer' ,0 ,N'Nahkampfangriffe, die durch Waffen aus dem Element (bei Erz insbesondere alle Metallwaffen, bei Humus Holzwaffen) durchgeführt werden, müssen bei einem Treffer sowie bei einer gelungenen PA einen BF-Test machen, um nicht zu zerbrechen. Sehr selten und spezialisiert sind auch die Varianten für Feuer (gegen Waffen aus Obsidian, Feuerstein und bestimmte Vulkangesteine) sowie gegen Waffen aus Eis.' ,N'EG 146');
UPDATE [Kampfregel] SET [Bemerkung] = N'Übliche Angriffsform. Tiere der Größenklasse groß können nur mit Schild pariert werden. Sehr große Tieren können nicht pariert werden. Lediglich Gegenhalten und Klingenhand sind möglich.' ,[Literatur] = N'WdS 90, 91, 113 / ZBA 11' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000002';
UPDATE [Kampfregel] SET [Name] = N'Doppelangriff (Tier)' ,[Bemerkung] = N'Zwei AT +4, die sich gegen EINEN Gegner richtet. Gelingt nur eine AT so ist der Angriff misslungen. Die eine AT kommt zur Wirkung. Gelingt Doppelangriff so kann nur (meisterlich) ausgewichen werden. Die SP werden separat ermittelt.' ,[Literatur] = N'WdS 113 / ZBA 12' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000003';
UPDATE [Kampfregel] SET [Bemerkung] = N'Nach AT fliegt das Tier wieder außer Reichweite. Schilde eignen sich besonders gut gegen fliegende Tiere, daher wird die PA Erschwernis von Schilden nur halbiert. Schuss ins Kampfgetümmel +3 pro Beteiligtem. Regeln zur Überzahl sind hierbei außer Kraft gesetzt.' ,[Literatur] = N'WdS 113 / ZBA 11' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000004';
UPDATE [Kampfregel] SET [Bemerkung] = N'Entspricht einer glücklichen AT/ Umgeht den RS des Opfers.' ,[Literatur] = N'WdS 113 / ZBA 11' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000005';
UPDATE [Kampfregel] SET [Bemerkung] = N'Die in Klammern stehende Zahl gibt die Erschwernis der IN Probe an. Misslingt diese so darf das Opfer in der ersten KR nichts tun. Das Tier erhält in dieser KR ein Bonus von +5 auf die AT.' ,[Literatur] = N'WdS 113 / ZBA 11' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000006';
UPDATE [Kampfregel] SET [Bemerkung] = N'Befreiung ist um den in Klammern stehenden Wert mittels einer GE-Probe erschwert. Für jede Misslungene Probe erhöt sich der Wert um 1. Gleichzeitig gilt der Malus auch für die Kampfwerte des Opfers.' ,[Literatur] = N'WdS 113 / ZBA 11' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000007';
UPDATE [Kampfregel] SET [Name] = N'Niederwerfen' ,[Bemerkung] = N'AT+4+X (X=zusätzliche Erschwernis auf KK-Probe), bei Erfolg: Opfer –2W6 INI und am Boden.Wenn die AT nicht erfolgreich durch (meisterliches) ausweichen  (Waffen- und Schildparaden verhindern nur den Schaden, nicht das umwerfen) oder eine waffenlose PA mit Manöver Auspendeln bzw. Beinarbeit ( bei Tieren bis max. mittlerer Größe) abgewehrt wird muss eine KK bzw. GE erschwert durch BE und den in Klammern angegeben Wert abgelegt werden' ,[Literatur] = N'WdS 63, 75, 114 / ZBA 12' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000008';
UPDATE [Kampfregel] SET [Bemerkung] = N'Normale AT. Bei besonders kräftigen Tieren gilt der Angriff zum Niederwerfen. Diese Angriffsform kann von Tieren der Größenklasse groß nur mit Schild pariert werden, von sehr großen gar nicht. Gegenhalten und Klingenwand sind bei großen Tieren möglich.' ,[Literatur] = N'WdS 114 / ZBA 12' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000009';
UPDATE [Kampfregel] SET [Bemerkung] = N'Subsumiert werden: Tollwut, Kampfrausch, Blutrausch u. ä.. Die PA sind um den angebenen Wert, AT und TP steigen dementsprechend.' ,[Literatur] = N'WdS 114 / ZBA 12' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000010';
UPDATE [Kampfregel] SET [Bemerkung] = N'Nach AT fliegt das Tier wieder außer Reichweite. Schilde eignen sich besonders gut gegen Angriff Sturzflug, daher wird die PA Erschwernis von Schilden wie bei fliegenden Tieren behandelt. Schuss ins Kampfgetümmel +6 pro Beteiligtem. Regeln zur Überzahl sind hierbei außer Kraft gesetzt. Das fliegende Tier hat stehts die Initiative. Seine AT steig um +3/ die PA sinkt um  +3.' ,[Literatur] = N'WdS 114 / ZBA 11' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000011';
UPDATE [Kampfregel] SET [Bemerkung] = N'Bedingung: Verteidiger liegt am Boden. Nach einem Überrennen kostet Trampeln eine Aktion. Der Verteidiger muss mittels einer Ausweichen +8 abwehren.' ,[Literatur] = N'WdS 114 / ZBA 12' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000012';
UPDATE [Kampfregel] SET [Name] = N'Verbeißen' ,[Bemerkung] = N'Nur möglich in DK H. Jede KR werden automatisch TP ermittelt die nicht parierbar sind. Angriffe des Opfers sind möglich. Das Tier richtet echte TP an. Die SP summieren sich. Bei mehr als KO entsteht eine Wunde. Tier beendet Angriff erst wenn es eine Wunde erlitten hat. Ausgenommen Tiere die ihre Brut verteidigen oder in Enge getrieben worden sind.' ,[Literatur] = N'WdS 114 / ZBA 11' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000013';
UPDATE [Kampfregel] SET [Name] = N'Umklammern' ,[Bemerkung] = N'Nur möglich in DK H. Jede KR werden automatisch TP ermittelt die nicht parierbar sind. Angriffe des Opfers sind möglich. Das Tier richtet echte TP an. Die SP summieren sich. Bei mehr als KO entsteht eine Wunde. Tier beendet Angriff erst wenn es eine Wunde erlitten hat. Um Angriff zu brechen muss eine KK und eine GE-Probe gelingen die zusätzlich erschwert ist (siehe Angaben). Das Opfer kann eine TaW Ringen Probe ablegen um Probe zu erleichtern. TaW* sind auf AT/PA zu verteilen. Schüsse ins Kampfgetümmel werden normal abgehandelt.' ,[Literatur] = N'WdS 114 / ZBA 11' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000014';
UPDATE [Kampfregel] SET [Bemerkung] = N'Zählt automatisch als Angriff zum Niederwerfen. Das Opfer kann nur (meisterlich) ausweichen, u. U. auch Gegenhalten (hierbei sollte beachtet werdern dass der halbe Schaden jedoch sehr hoch sein kann und es ein Angriff zum Niederwerfen bleibt).' ,[Literatur] = N'WdS 114 / ZBA 12' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000015';
UPDATE [Kampfregel] SET [Name] = N'Angriff mit (pervertiertem) Element' ,[Bemerkung] = N'Das Element, bzw das pervertierte Gegenstück kann zum Angriff genutzt werden, der Effekt entspricht bei Elementaren dem ZORN DER ELEMENTE, die Wirkung der Unelemente ist ähnlich, aber nach Meisterentscheid chaotischer, AT-Wurf des Wesens, ohne Reichweitenangabe ist diese 7 Schritt, nur gutes Ausweiche (halber Ausweichen-Wert) möglich' ,[Literatur] = N'WdZ 232 / TCD 152' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000016';
UPDATE [Kampfregel] SET [Bemerkung] = N'Wirkung ähnlich dem OCULUS ASTRALIS, kann selbst in Dunkelheit zur groben Orientierung genutzt werden, TaW ist normalerweise 12, HELLSICHT TRÜBEN, SCHLEIER DER UNWISSENHEIT können den Sinn täuschen' ,[Literatur] = N'WdZ 232 / TCD 152' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000017';
UPDATE [Kampfregel] SET [Bemerkung] = N'Dämon kann als Abwehrreaktion in den Limbus ausweichen und so mehreren Angriffen gleichzeitig entgehen, verbleibt 1W6 KR im Limbus, kann dort mit Regeneration im Limbus 1W6 LeP/KR regenerieren und kommt maximal 1 Schritt vom Ort entfernt wieder raus, erst nach W6 KR wieder nutzbar, geht auch gegen Zauber, nicht jedoch gegen Austreibung' ,[Literatur] = N'WdZ 232 / TCD 153' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000018';
UPDATE [Kampfregel] SET [Bemerkung] = N'Dämon kann in das Element eintauch und sich mit GS 1 bewegen, nicht bei Objekten die mit antimagischer Zauberei, expliziten Schutzkreisen, karmaler Aufladung oder Koschbasalt geschützt sind möglich, im Element sind normale Wahrnehmung und Beobachtung aus dem Element heraus möglich' ,[Literatur] = N'WdZ 235 / TCD 153' WHERE [KampfregelGUID]='00000000-0000-0000-0009-000000000019';
