		ON UPDATE CASCADE ON DELETE CASCADE
)
GO

CREATE TABLE [Elementarwesen] (
	[GegnerBaseGUID] uniqueidentifier NOT NULL PRIMARY KEY, 
	[Element] nvarchar(100), 
