GO
UPDATE Held_VorNachteil SET Wert = '' WHERE Wert IS NULL
GO
ALTER TABLE Held_VorNachteil ALTER COLUMN Wert nvarchar(1200) NOT NULL
GO
ALTER TABLE Held_VorNachteil DROP CONSTRAINT PK_Held_VorNachteil
GO
ALTER TABLE Held_VorNachteil ADD Id bigint NOT NULL IDENTITY
GO
ALTER TABLE Held_VorNachteil ADD CONSTRAINT PK_Held_VorNachteil PRIMARY KEY (Id)
GO
CREATE UNIQUE INDEX UQ_Held_VorNachteil ON Held_VorNachteil (
	HeldGUID, VorNachteilGUID, Wert
)
GO

